import React, {useState, useEffect} from 'react'
import axios from 'axios'

function TodoList() {

    const [items, setItems] = useState([]);

    // GET all & save to state
    useEffect(() => {
      axios.get('http://localhost:8080/api/items')
      .then(res => {setItems(res.data)})
      .catch((error) => console.log(error))
    }, [items])

    // DELETE by id
    const handleDelete = async (id) => {
        axios.delete(`http://localhost:8080/api/items/${id}`)
        .then(response => {console.log(response)})
        .catch((error) => console.log(error))
    };

    // UPDATE (tick if done)
    const handleUpdate = async (id) => {
        axios.put(`http://localhost:8080/api/items/${id}`, {
            done: true
        })
        .then(response => {console.log(response)})
        .catch((error) => console.log(error))
    };
      
    return (
        <div className="todo-list">
            <div className="all-items">
                {items.map((item) => {
                    return <div key={item.id} className="item">
                        <img className="circle" src="\images\gray-circle-outline-png.png" alt="circle" onClick={() => handleUpdate(item.id)}></img>
                        {item.done ? <img className="tick" src="/images/icon-check.svg" alt="tick" onClick={() => handleUpdate(item.id)}></img> : null}
                        <p> {item.content} </p>
                        <img className="cross" src="\images\icon-cross.svg" alt="cross" onClick={() => handleDelete(item.id)}></img>
                    </div>
                })}
            </div>
            <div className="bottom-row">
                <p>{items.length} items left</p>
                <p>Clear completed</p>
            </div>
        </div>
    )
}

export default TodoList
