import React, {useState, useEffect} from 'react'
import axios from 'axios'

function TodoList() {

    const [items, setItems] = useState([]);

// Fetching from backend
/* const getAll = async () => {
    try {
        const res = await fetch('http://localhost:8080/api/items', {
            method: 'GET',
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
        });
        return await res.json();
    } catch (err) {}
  }  */
  // Save fetched data to state
/*   useEffect(() => {
    getAll()
    .then((res) => {
      setItems(res);
    })
    .catch((error) => console.log(error))
  }, [items]); */

  useEffect(() => {
    axios.get('http://localhost:8080/api/items')
    .then(res => {
        setItems(res.data);
    })
    .catch((error) => console.log(error))

  }, [items])


    // Delete
    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:8080/api/items/${id}`)
        .then(console.log('Deleted'))
        .catch((error) => console.log(error))
    };
  
    return (
        <div className="todo-list">
            <div className="all-items">
                {items.map((item) => {
                    return <div key={item.id} className="item">
                        <img className="circle" src="\images\gray-circle-outline-png.png" alt="circle"></img>
                        <p> {item.content} </p>
                        <img className="cross" src="\images\icon-cross.svg" alt="cross" onClick={() => handleDelete(item.id)}></img>
                    </div>
                })}
            </div>
            <div className="bottom-row">
                <p>{items.length} items left</p>
                <p>Clear completed</p>
            </div>
        </div>
    )
}

export default TodoList
